    // Listen for messages from the player
    if (window.addEventListener) {
        window.addEventListener('message', onMessageReceived, false);
    } else {
        window.attachEvent('onmessage', onMessageReceived, false);
    }

    // Handle messages received from the player
    function onMessageReceived(e) {
        if (e.origin !== "http://player.vimeo.com") {
            return;
        }

        if ( typeof(_gaq) != 'undefined' ) {
            gatype = 1; // ga.js
        } else if ( typeof(window.ga) != 'undefined' ) {
            gatype = 2; // analytics.js
        }

        if (gatype == 0) {
            return; // Google Analytics not found
        }

        var data = JSON.parse(e.data);

        switch (data.event) {
        case 'ready':
            onReady();
            break;

        case 'playProgress':
            onPlayProgress(data.data);
            break;

        case 'seek':
            if (trackSeeking && !videoSeeking) {
                if ( gatype == 1 ) {
                    _gaq.push(['_trackEvent', 'video', 'Skipped video forward or backward', url, undefined, false]);
