                    ga('send', 'event', 'video', 'Skipped video forward or backward', url);
                }
                videoSeeking = true; // Avoid subsequent seek trackings
            }
            break;

        case 'play':
            if (!videoPlayed) {
                if ( gatype == 1 ) {
                    _gaq.push(['_trackEvent', 'video', 'Started', url, undefined, false]);             
