                    ga('send', 'event', 'video', 'Started', url);
                }
                videoPlayed = true; //  Avoid subsequent play trackings
            }
            break;

        case 'pause':
            onPause();
            break;

        case 'finish':
            if (!videoCompleted) {
                if ( gatype == 1 ) {
                    _gaq.push(['_trackEvent', 'video', 'Completed', url, undefined, false]);       
                } else if ( gatype == 2 ) {
                    ga('send', 'event', 'video', 'Completed', url);
                }
                videoCompleted = true; // Avoid subsequent finish trackings
            }
            break;
        }
    }

    // Helper function for sending a message to the player
    function post(action, value) {
        var data = {
            method: action
        };

        if (value) {
            data.value = value;
        }

        f[0].contentWindow.postMessage(JSON.stringify(data), url);
    }

    function onReady() {
        post('addEventListener', 'play');
        post('addEventListener', 'seek');
        post('addEventListener', 'pause');
        post('addEventListener', 'finish');
        post('addEventListener', 'playProgress');
